mapper.update:
update follow
set fol_uid = #{folUid,jdbcType=INTEGER},
  fol_userid = #{folUserid,jdbcType=INTEGER},
  fol_createtime = #{folCreatetime,jdbcType=TIMESTAMP},
  fol_status = #{folStatus,jdbcType=INTEGER}
where fol_id = #{folId,jdbcType=INTEGER}

mapper.select:
select * from follow where fol_uid=#{folUid}

  and fol_status=#{folStatus}

order by fol_createtime desc
