mapper.insert:
insert into massage (msg_id, msg_suid, msg_text, 
  msg_createtime, msg_ruid, msg_status
  )
values (#{msgId,jdbcType=INTEGER}, #{msgSuid,jdbcType=INTEGER}, #{msgText,jdbcType=VARCHAR}, 
  #{msgCreatetime,jdbcType=TIMESTAMP}, #{msgRuid,jdbcType=INTEGER}, #{msgStatus,jdbcType=INTEGER}
  )

mapper.select:
select * from massage where 1=1

  and msg_ruid=#{msgRuid}


  and msg_status=#{msgStatus}

  order by msg_createtime desc
